import React, { useState } from "react";
import DOMPurify from 'dompurify';

export const Register = (props) => {
    const [name, setName] = useState('');
    const [idNumber, setIdNumber] = useState('');
    const [accountNumber, setAccountNumber] = useState('');
    const [pass, setPass] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        const fullAccountNumber = `cust${accountNumber}`;
        
        const response = await fetch('http://localhost:3000/api/users/register', 
        {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            credentials: 'include',
            body: JSON.stringify({ fullName: name, idNumber, accountNumber: fullAccountNumber, password: pass }),
        });

        const data = await response.text();
        if (response.status === 201) {
            alert('Registration successful!');
            props.onFormSwitch('login');
        } else {
            alert('Registration failed. Please try again.');
        }
        console.log(data);
    };

    const handleNameChange = (e) => {
        const value = e.target.value;
        if (/^[a-zA-Z\s]*$/.test(value)) {
            setName(value);
        }
    };

    const handleIdNumberChange = (e) => {
        const value = e.target.value;
        if (/^\d*$/.test(value)) {
            setIdNumber(value);
        }
    };

    const handleAccountNumberChange = (e) => {
        const value = e.target.value;
        if (/^\d*$/.test(value)) {
            setAccountNumber(value);
        }
    };

    const handlePasswordChange = (e) => {
        const value = e.target.value;
        if (/^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/.test(value)) {
            setPass(value);
        } else {
            alert("Password must be at least 8 characters long and include at least one letter and one number.");
        }
    };

    return (
        <div className="auth-form-container">
            <h2>Register</h2>
            <form className="register-form" onSubmit={handleSubmit}>
                <label htmlFor="name">Full Name</label>
                <input
                    value={DOMPurify.sanitize(name)}
                    onChange={handleNameChange}
                    name="name"
                    id="name"
                    placeholder="John Doe"
                    required
                />
                <label htmlFor="idNumber">ID Number</label>
                <input
                    value={DOMPurify.sanitize(idNumber)}
                    onChange={handleIdNumberChange}
                    type="text"
                    placeholder="Your ID Number"
                    id="idNumber"
                    name="idNumber"
                    required
                />
                <label htmlFor="accountNumber">Account Number</label>
                <input
                    value={accountNumber}
                    onChange={handleAccountNumberChange}
                    type="text"
                    placeholder="Account Number (e.g., 0000)"
                    id="accountNumber"
                    name="accountNumber"
                    required
                />
                <label htmlFor="password">Password</label>
                <input
                    value={pass}
                    onChange={handlePasswordChange}
                    type="password"
                    placeholder="***********"
                    id="password"
                    name="password"
                    required
                />
                <button type="submit">Register</button>
            </form>
            <button className="link-btn" onClick={() => props.onFormSwitch('login')}>
                Already have an account? Log In
            </button>
        </div>
    );
};
