import React, { useEffect, useState } from 'react';
import './EmployeesDashboard.css';

export const EmployeesDashboard = ({ onFormSwitch }) => {
    const [payments, setPayments] = useState([]);

    useEffect(() => {
        // Fetch payment data from the server
        const fetchPayments = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/payments');
                const data = await response.json();
                setPayments(data);
            } catch (error) {
                console.error('Error fetching payments:', error);
            }
        };

        fetchPayments();
    }, []);

    const handleLogout = () => {
        onFormSwitch('login');
        alert('You have been logged out.');
    };

    return (
        <div className="employees-dashboard">
            <nav className="navbar">
                <div className="navbar-right">
                    <span className="nav-link logout" onClick={handleLogout}>Log Out</span>
                </div>
            </nav>
            <h1>Employees Dashboard</h1>
            <table className="employees-table">
                <thead>
                    <tr>
                        <th>Object ID</th>
                        <th>Amount</th>
                        <th>Currency</th>
                        <th>SWIFT Code</th>
                        <th>Date</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {payments.map(payment => (
                        <tr key={payment._id}>
                            <td>{payment._id}</td>
                            <td>{payment.amount}</td>
                            <td>{payment.currency}</td>
                            <td>{payment.swiftCode}</td>
                            <td>{new Date(payment.date).toLocaleString()}</td>
                            <td>
                                <button>Verify</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button className="submit-button">Submit</button>
        </div>
    );
};
