import React, { useState } from "react";
import DOMPurify from 'dompurify';  // For XSS prevention

export const Register = (props) => {
    const [name, setName] = useState('');
    const [idNumber, setIdNumber] = useState('');
    const [accountNumber, setAccountNumber] = useState('');
    const [pass, setPass] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        // Prepend 'cust' to the account number
        const fullAccountNumber = `cust${accountNumber}`;

        // Send the registration request
        const response = await fetch('http://localhost:3000/api/users/register', 
        {
            method: 'POST',
            headers: 
            {
                'Content-Type': 'application/json',
            },
            credentials: 'include', // Ensure cookies are included in the request
            body: JSON.stringify({ fullName: name, idNumber, accountNumber: fullAccountNumber, password: pass }),
        });

        const data = await response.text();
        if (response.status === 201) 
        {
            alert('Registration successful!');
            props.onFormSwitch('login');
        } 
        else 
        {
            alert('Registration failed. Please try again.');
        }
        console.log(data);
    };    

    // Validate input patterns
    const handleNameChange = (e) => {
        const value = e.target.value;
        if (/^[a-zA-Z\s]*$/.test(value)) { // Allow only letters and spaces
            setName(value);
        }
    };

    const handleIdNumberChange = (e) => {
        const value = e.target.value;
        if (/^\d{13}$/.test(value)) { // South African ID number format (13 digits)
            setIdNumber(value);
        }
    };

    const handleAccountNumberChange = (e) => {
        const value = e.target.value;
        if (/^\d{4}$/.test(value)) { // Allow only 4-digit numbers
            setAccountNumber(value);
        }
    };

    const handlePasswordChange = (e) => {
        const value = e.target.value;
        if (/^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{8,}$/.test(value)) { // At least 8 characters, including letters and numbers
            setPass(value);
        } else {
            alert('Password must be at least 8 characters long and include both letters and numbers.');
        }
    };

    return (
        <div className="auth-form-container">
            <h2>Register</h2>
            <form className="register-form" onSubmit={handleSubmit}>
                <label htmlFor="name">Full Name</label>
                <input
                    value={DOMPurify.sanitize(name)} // Sanitize the input to prevent XSS
                    onChange={handleNameChange}
                    name="name"
                    id="name"
                    placeholder="John Doe"
                />
                <label htmlFor="idNumber">ID Number</label>
                <input
                    value={DOMPurify.sanitize(idNumber)} // Sanitize the input to prevent XSS
                    onChange={handleIdNumberChange}
                    type="text"
                    placeholder="Your ID Number"
                    id="idNumber"
                    name="idNumber"
                />
                <label htmlFor="accountNumber">Account Number</label>
                <input
                    value={accountNumber} // No need to sanitize since input is restricted
                    onChange={handleAccountNumberChange}
                    type="text"
                    placeholder="Account Number (e.g 0000)"
                    id="accountNumber"
                    name="accountNumber"
                />
                <label htmlFor="password">Password</label>
                <input
                    value={pass}
                    onChange={handlePasswordChange}
                    type="password"
                    placeholder="***********"
                    id="password"
                    name="password"
                />
                <button type="submit">Register</button>
            </form>
            <button className="link-btn" onClick={() => props.onFormSwitch('login')}>
                Already have an account? Log In
            </button>
        </div>
    );
};
