import React, { useState } from 'react';
import './Dash.css';

export const Dashboard = (props) => {
    const [amount, setAmount] = useState('');
    const [currency, setCurrency] = useState('USD');
    const [provider, setProvider] = useState('SWIFT');
    const [accountInfo, setAccountInfo] = useState('');
    const [swiftCode, setSwiftCode] = useState('');

    const handleLogout = () => {
        props.onFormSwitch('login');
        alert('You have been logged out.');
    };

    const handlePaymentSubmit = (e) => {
        e.preventDefault();
        // Implement payment submission logic here
        alert(`Payment of ${amount} ${currency} has been submitted using ${provider}.`);
    };

    return (
        <div className="dash-container">
            <nav className="navbar">
                <div className="navbar-right">
                    <span className="nav-link logout" onClick={handleLogout}>Log Out</span>
                </div>
            </nav>
            <div className="dashboard-content">
                <h1>Payment Portal</h1>
                <form className="payment-form" onSubmit={handlePaymentSubmit}>
                    <label htmlFor="amount">Amount</label>
                    <input
                        type="number"
                        id="amount"
                        name="amount"
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                        placeholder="Enter amount"
                        required
                    />
                    
                    <label htmlFor="currency">Currency</label>
                    <select
                        id="currency"
                        name="currency"
                        value={currency}
                        onChange={(e) => setCurrency(e.target.value)}
                    >
                        <option value="USD">USD</option>
                        <option value="EUR">EUR</option>
                        <option value="GBP">GBP</option>
                        <option value="ZAR">ZAR</option> {/* South African Rand */}
                        {/* Add more currencies as needed */}
                    </select>

                    <label htmlFor="provider">Provider</label>
                    <select
                        id="provider"
                        name="provider"
                        value={provider}
                        onChange={(e) => setProvider(e.target.value)}
                    >
                        <option value="SWIFT">SWIFT</option>
                        {/* Add more providers if available */}
                    </select>

                    <label htmlFor="accountInfo">Account Information</label>
                    <input
                        type="text"
                        id="accountInfo"
                        name="accountInfo"
                        value={accountInfo}
                        onChange={(e) => setAccountInfo(e.target.value)}
                        placeholder="Enter account information"
                        required
                    />

                    <label htmlFor="swiftCode">SWIFT Code</label>
                    <input
                        type="text"
                        id="swiftCode"
                        name="swiftCode"
                        value={swiftCode}
                        onChange={(e) => setSwiftCode(e.target.value)}
                        placeholder="Enter SWIFT code"
                        required
                    />

                    <button type="submit">Pay Now</button>
                </form>
            </div>
        </div>
    );
};
