import React, { useEffect, useState } from 'react';
import './EmployeesDash.css';

export const EmployeesDashboard = ({ onFormSwitch }) => {
    const [payments, setPayments] = useState([]); // Initialize as an empty array
    const [error, setError] = useState(null);

    useEffect(() => {
        // Fetch payment data from the server
        const fetchPayments = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/payments');
                if (!response.ok) {
                    throw new Error('Failed to fetch payments');
                }
                const data = await response.json();
                setPayments(Array.isArray(data) ? data : []); // Ensure data is an array
            } catch (error) {
                console.error('Error fetching payments:', error);
                setError('Error fetching payments');
                setPayments([]); // Set to an empty array in case of an error
            }
        };

        fetchPayments();
    }, []);

    const handleLogout = () => {
        onFormSwitch('login');
        alert('You have been logged out.');
    };

    return (
        <div className="employees-dashboard">
            <nav className="navbar">
                <div className="navbar-right">
                    <span className="nav-link logout" onClick={handleLogout}>Log Out</span>
                </div>
            </nav>
            <h1>Employees Dashboard</h1>
            {error ? (
                <p>{error}</p> // Display error message if there’s an error
            ) : (
                <table className="employees-table">
                    <thead>
                        <tr>
                            <th>Object ID</th>
                            <th>Amount</th>
                            <th>Currency</th>
                            <th>SWIFT Code</th>
                            <th>Date</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {payments.map(payment => (
                            <tr key={payment._id}>
                                <td>{payment._id}</td>
                                <td>{payment.amount}</td>
                                <td>{payment.currency}</td>
                                <td>{payment.swiftCode}</td>
                                <td>{new Date(payment.date).toLocaleString()}</td>
                                <td>
                                    <button>Verify</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
            <button className="submit-button">Submit</button>
        </div>
    );
};
