import React, { useState } from 'react';
import './Dash.css';

export const Dashboard = (props) => {
    const [idNumber, setIdNumber] = useState(''); // New field for ID number
    const [accountNumber, setAccountNumber] = useState(''); // New field for Account number
    const [cardHolder, setCardHolder] = useState('');
    const [amount, setAmount] = useState('');
    const [currency, setCurrency] = useState('USD');
    const [swiftCode, setSwiftCode] = useState('');

    const handleLogout = () => {
        props.onFormSwitch('login');
        alert('You have been logged out.');
    };

    const handlePaymentSubmit = async (e) => {
        e.preventDefault();

        // Prepare the data to be sent
        const paymentData = {
            idNumber,
            accountNumber,
            cardHolder,
            amount,
            currency,
            swiftCode
        };

        try {
            const response = await fetch('http://localhost:3000/api/payments', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(paymentData),
            });

            if (response.ok) {
                alert('Payment submitted successfully!');
            } else {
                alert('Failed to submit payment.');
            }
        } catch (error) {
            console.error('Error submitting payment:', error);
            alert('Error submitting payment.');
        }
    };

    return (
        <div className="dash-container">
            <nav className="navbar">
                <div className="navbar-right">
                    <span className="nav-link logout" onClick={handleLogout}>Log Out</span>
                </div>
            </nav>
            <div className="dashboard-content">
                <h1>Payment Portal</h1>
                <form className="payment-form" onSubmit={handlePaymentSubmit}>
                    <div className="form-left">
                        <label htmlFor="amount">Amount</label>
                        <input
                            type="number"
                            id="amount"
                            name="amount"
                            value={amount}
                            onChange={(e) => setAmount(e.target.value)}
                            placeholder="Enter amount"
                            required
                        />
                        
                        <label htmlFor="currency">Currency</label>
                        <select
                            id="currency"
                            name="currency"
                            value={currency}
                            onChange={(e) => setCurrency(e.target.value)}
                        >
                            <option value="USD">USD</option>
                            <option value="EUR">EUR</option>
                            <option value="GBP">GBP</option>
                            <option value="ZAR">ZAR</option> {/* South African Rand */}
                        </select>

                        <label htmlFor="idNumber">ID Number</label>
                        <input
                            type="text"
                            id="idNumber"
                            name="idNumber"
                            value={idNumber}
                            onChange={(e) => setIdNumber(e.target.value)}
                            placeholder="Your ID Number"
                            required
                        />

                        <label htmlFor="accountNumber">Account Number</label>
                        <input
                            type="text"
                            id="accountNumber"
                            name="accountNumber"
                            value={accountNumber}
                            onChange={(e) => setAccountNumber(e.target.value)}
                            placeholder="Your Account Number"
                            required
                        />
                    </div>

                    <div className="form-right">
                        <label htmlFor="cardHolder">Card Holder Name</label>
                        <input
                            type="text"
                            id="cardHolder"
                            name="cardHolder"
                            value={cardHolder}
                            onChange={(e) => setCardHolder(e.target.value)}
                            placeholder="Card holder name"
                            required
                        />

                        <label htmlFor="swiftCode">SWIFT Code</label>
                        <input
                            type="text"
                            id="swiftCode"
                            name="swiftCode"
                            value={swiftCode}
                            onChange={(e) => setSwiftCode(e.target.value)}
                            placeholder="Enter SWIFT code"
                            required
                        />
                    </div>
                    
                    <button type="submit">Pay Now</button>
                </form>
            </div>
        </div>
    );
};
