import React, { useState } from 'react';
import './EmployeesDash.css'; // Assuming you'll create CSS for styling

export const EmployeesDashboard = ({ onFormSwitch }) => {
    // Placeholder data for the table
    const [rows, setRows] = useState([
        { id: 1, name: 'John Doe', amount: 100, currency: 'USD', swiftCode: 'ABC123', verified: false },
        { id: 2, name: 'Jane Smith', amount: 200, currency: 'EUR', swiftCode: 'DEF456', verified: false },
        { id: 3, name: 'Bob Johnson', amount: 300, currency: 'GBP', swiftCode: 'GHI789', verified: false }
    ]);

    const handleVerify = (id) => {
        setRows(rows.map(row => row.id === id ? { ...row, verified: true } : row));
    };

    const handleSubmit = () => {
        alert('Submit button clicked. Implement submission logic here.');
    };

    return (
        <div className="employees-dashboard">
            <h1>Employees Dashboard</h1>
            
            <nav className="navbar">
                <div className="navbar-right">
                    <span className="nav-link logout" onClick={() => onFormSwitch('login')}>Log Out</span>
                </div>
            </nav>
            
            <table className="employees-table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Amount</th>
                        <th>Currency</th>
                        <th>SWIFT Code</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {rows.map(row => (
                        <tr key={row.id}>
                            <td>{row.name}</td>
                            <td>{row.amount}</td>
                            <td>{row.currency}</td>
                            <td>{row.swiftCode}</td>
                            <td>
                                <button onClick={() => handleVerify(row.id)} disabled={row.verified}>
                                    {row.verified ? 'Verified' : 'Verify'}
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button className="submit-button" onClick={handleSubmit}>Submit</button>
        </div>
    );
};
