import React, { useState } from "react";
import DOMPurify from 'dompurify';  // For XSS prevention

export const Login = (props) => {
    const [idNumber, setIdNumber] = useState('');
    const [accountNumber, setAccountNumber] = useState('');
    const [pass, setPass] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Send the login request
        const response = await fetch('http://localhost:3000/api/users/login',  
        {
            method: 'POST',
            headers: 
            {
                'Content-Type': 'application/json',
            },
            credentials: 'include', // Ensure cookies are included in the request
            body: JSON.stringify({ idNumber, accountNumber, password: pass }),
        });

        const data = await response.text();
        if (response.status === 200) 
        {
            alert('Login successful!');
            if (accountNumber.startsWith('cust')) {
                props.onFormSwitch('dashboard'); // Redirect to customer dashboard
            } else if (accountNumber.startsWith('emp')) {
                props.onFormSwitch('employeesDashboard'); // Redirect to employee dashboard
            }
        } 
        else 
        {
            alert('Invalid credentials. Please try again.');
        }
        console.log(data);
    };

    return (
        <div className="auth-form-container">
            <h2>Login</h2>
            <form className="login-form" onSubmit={handleSubmit}>
                <label htmlFor="idNumber">ID Number</label>
                <input
                    value={DOMPurify.sanitize(idNumber)} // Sanitize the input to prevent XSS
                    onChange={(e) => setIdNumber(e.target.value)}
                    type="text"
                    placeholder="Your ID Number"
                    id="idNumber"
                    name="idNumber"
                />
                <label htmlFor="accountNumber">Account Number</label>
                <input
                    value={DOMPurify.sanitize(accountNumber)} // Sanitize the input to prevent XSS
                    onChange={(e) => setAccountNumber(e.target.value)}
                    type="text"
                    placeholder="Your Account Number"
                    id="accountNumber"
                    name="accountNumber"
                />
                <label htmlFor="password">Password</label>
                <input
                    value={pass}
                    onChange={(e) => setPass(e.target.value)}
                    type="password"
                    placeholder="***********"
                    id="password"
                    name="password"
                />
                <button type="submit">Log In</button>
            </form>
            <button className="link-btn" onClick={() => props.onFormSwitch('register')}>
                Don't have an account? Register
            </button>
        </div>
    );
};
