import React, { useState, useEffect } from 'react';
import './Dash.css';

export const Dashboard = (props) => {
    // Pre-fill from login details
    const [idNumber, setIdNumber] = useState(props.idNumber || '');
    const [accountNumber, setAccountNumber] = useState(props.accountNumber || '');
    const [amount, setAmount] = useState('');
    const [currency, setCurrency] = useState('USD');
    const [swiftCode, setSwiftCode] = useState('');
    const [cardHolder, setCardHolder] = useState('');
    const [cardNumber, setCardNumber] = useState('');
    const [expiryDate, setExpiryDate] = useState('');
    const [cvv, setCvv] = useState('');

    useEffect(() => {
        if (props.idNumber) setIdNumber(props.idNumber);
        if (props.accountNumber) setAccountNumber(props.accountNumber);
    }, [props.idNumber, props.accountNumber]);

    const handleLogout = () => {
        props.onFormSwitch('login');
        alert('You have been logged out.');
    };

    const handlePaymentSubmit = async (e) => {
        e.preventDefault();

        // Prepare only the required data to be sent to the backend
        const paymentData = {
            idNumber,
            accountNumber,
            amount,
            currency,
            swiftCode
        };

        try {
            const response = await fetch('http://localhost:3000/api/payments', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(paymentData),
            });

            if (response.ok) {
                alert('Payment submitted successfully!');
            } else {
                alert('Failed to submit payment.');
            }
        } catch (error) {
            console.error('Error submitting payment:', error);
            alert('Error submitting payment.');
        }
    };

    return (
        <div className="dash-container">
            <nav className="navbar">
                <div className="navbar-right">
                    <span className="nav-link logout" onClick={handleLogout}>Log Out</span>
                </div>
            </nav>
            <div className="dashboard-content">
                <h1>Payment Portal</h1>
                <form className="payment-form" onSubmit={handlePaymentSubmit}>
                    <div className="form-left">
                        <label htmlFor="amount">Amount</label>
                        <input
                            type="number"
                            id="amount"
                            name="amount"
                            value={amount}
                            onChange={(e) => setAmount(e.target.value)}
                            placeholder="Enter amount"
                            required
                        />
                        
                        <label htmlFor="currency">Currency</label>
                        <select
                            id="currency"
                            name="currency"
                            value={currency}
                            onChange={(e) => setCurrency(e.target.value)}
                        >
                            <option value="USD">USD</option>
                            <option value="EUR">EUR</option>
                            <option value="GBP">GBP</option>
                            <option value="ZAR">ZAR</option> {/* South African Rand */}
                        </select>

                        <label htmlFor="swiftCode">SWIFT Code</label>
                        <input
                            type="text"
                            id="swiftCode"
                            name="swiftCode"
                            value={swiftCode}
                            onChange={(e) => setSwiftCode(e.target.value)}
                            placeholder="Enter SWIFT code"
                            required
                        />
                    </div>

                    <div className="form-right">
                        <label htmlFor="cardHolder">Card Holder Name</label>
                        <input
                            type="text"
                            id="cardHolder"
                            name="cardHolder"
                            value={cardHolder}
                            onChange={(e) => setCardHolder(e.target.value)}
                            placeholder="Card holder name"
                            required
                        />

                        <label htmlFor="cardNumber">Card Number</label>
                        <input
                            type="text"
                            id="cardNumber"
                            name="cardNumber"
                            value={cardNumber}
                            onChange={(e) => setCardNumber(e.target.value)}
                            placeholder="Enter card number"
                            required
                        />

                        <label htmlFor="expiryDate">Expiry Date</label>
                        <input
                            type="month"
                            id="expiryDate"
                            name="expiryDate"
                            value={expiryDate}
                            onChange={(e) => setExpiryDate(e.target.value)}
                            required
                        />

                        <label htmlFor="cvv">CVV</label>
                        <input
                            type="text"
                            id="cvv"
                            name="cvv"
                            value={cvv}
                            onChange={(e) => setCvv(e.target.value)}
                            placeholder="CVV"
                            required
                        />
                    </div>
                    
                    <button type="submit">Pay Now</button>
                </form>
            </div>
        </div>
    );
};
